import React, { Component } from "react";
import axios from "axios";
import "../../App.css";
import { Link, Redirect } from "react-router-dom";
import { LogInPOST } from "../../Constants";

export default class SignIn extends Component {
  constructor(props) {
    super(props);
    this.state = {
      emailid: "",
      password: "",
      isLoggedIn: false,
    };
    this.onLogIn = this.onLogIn.bind(this);
    this.userLogIn = this.userLogIn.bind(this);
  }

  onLogIn = (event) => {
    event.preventDefault();

    const newUser = {
      emailid: this.state.emailid,
      password: this.state.password,
    };

    axios.post(LogInPOST, newUser).then((response) => {
      if (response.status === 200) {
        this.setState({ isLoggedIn: true });
      }
    });
  };

  userLogIn = (event) => {
    this.setState({
      [event.target.name]: event.target.value,
    });
  };

  render() {
    const { emailid, password, isLoggedIn } = this.state;
    if (isLoggedIn) {
      return <Redirect to={{ pathname: "/service-registry" }} />;
    }
    return (
      <div className="auth-wrapper">
        <div className="auth-inner">
          <form onSubmit={this.onLogIn}>
            <h3>Sign In</h3>

            <div className="form-group">
              <label>Email address</label>
              <input
                type="email"
                className="form-control"
                placeholder="Enter email"
                name="emailid"
                value={emailid}
                onChange={this.userLogIn}
              />
            </div>

            <div className="form-group">
              <label>Password</label>
              <input
                type="password"
                className="form-control"
                placeholder="Enter password"
                name="password"
                value={password}
                onChange={this.userLogIn}
              />
            </div>
            <p></p>
            {/*<Link to="/service-registry">*/}
            <button
              type="submit"
              className="btn btn-primary btn-block"
              onClick={onLogIn}
            >
              Submit
            </button>
            {/*</Link>*/}
          </form>
        </div>
      </div>
    );
  }
}
