import React, { Component } from "react";
import "../../App.css";
import { Link } from "react-router-dom";
import { Dropdown } from "semantic-ui-react";
import { CityOptions, ServerOptions } from "../../Constants";

export default class ServicerRegistry extends Component {
  constructor(props) {
    super(props);
    this.state = {
      cityData: "",
      regionValue: "",
    };
  }

  handleCityDropdown = (value) => {
    this.setState({ cityData: value });
    console.log(this.state.cityData);
  };

  handleRegionDropdown = (value) => {
    this.setState({ regionValue: value });
  };

  render() {
    return (
      <div className="auth-wrapper">
        <div className="auth-inner">
          <form>
            <h2>Please Confirm your Region</h2>

            <div className="form-group">
              <p>Select the your location: </p>
              <Dropdown
                placeholder="Select City"
                fluid
                search
                selection
                clearable
                value
                options={CityOptions}
                onChange={this.handleCityDropdown}
              />
              <br />
              <p>Select the region: </p>
              <Dropdown
                placeholder="Select Region"
                fluid
                selection
                clearable
                value
                options={ServerOptions}
                onChange={this.handleRegionDropdown}
              />
            </div>
            <br />
            <Link to="/service-selected">
              <button
                type="submit"
                className="btn btn-primary btn-block"
                //onClick={submit}
              >
                Submit
              </button>
            </Link>
          </form>
        </div>
      </div>
    );
  }
}
